<?php if (!defined('BASEPATH')) exit('No direct script access allowed');function pdf_create($html, $filename, $stream = TRUE) {		require_once(APPPATH . 'helpers/dompdf/dompdf_config.inc.php');            $dompdf = new DOMPDF();        $dompdf->set_paper(array(0, 0, 612, 936), 'portrait'); //8.5 x 13                $dompdf->load_html($html);        $dompdf->render();        if ($stream) {                            $dompdf->stream($filename.".pdf",array('Attachment'=>0));//just view the created PDF file                //		//$dompdf->stream($filename.".pdf"); //download automatically the PDF file        }        else {    		$CI =& get_instance();        		$CI->load->helper('file');                    write_file('./uploads/temp/' . $filename . '.pdf', $dompdf->output());        }    }function pdf_create_letter($html, $filename, $stream = TRUE) {		require_once(APPPATH . 'helpers/dompdf/dompdf_config.inc.php');            $dompdf = new DOMPDF();        $dompdf->set_paper('letter', 'portrait'); //short                $dompdf->load_html($html);        $dompdf->render();        if ($stream) {                            $dompdf->stream($filename.".pdf",array('Attachment'=>0));//just view the created PDF file                //		//$dompdf->stream($filename.".pdf"); //download automatically the PDF file        }        else {    		$CI =& get_instance();        		$CI->load->helper('file');                    write_file('./uploads/temp/' . $filename . '.pdf', $dompdf->output());        }    }function pdf_create_a4($html, $filename, $stream = TRUE,$orientation = 'portrait') {		require_once(APPPATH . 'helpers/dompdf/dompdf_config.inc.php');            $dompdf = new DOMPDF();        $dompdf->set_paper('A4', $orientation); //a4                $dompdf->load_html($html);        $dompdf->render();        if ($stream) {                            $dompdf->stream($filename.".pdf",array('Attachment'=>0));//just view the created PDF file                //		//$dompdf->stream($filename.".pdf"); //download automatically the PDF file        }        else {		$dompdf->stream($filename.".pdf"); //download automatically the PDF file                        }    }